mapper.select:
select sum(id) from orders


        and order_time > #{begin}


        and #{end} > order_time


        and status = #{status}

select name from order_detail where order_id in
#{id}
group by name order by count(name) desc

select count(name) from order_detail where order_id in
#{id}
group by name order by count(name) desc

select * from orders


        and order_time > #{beginTime}


        and #{endTime} > order_time


        and number = #{number}


        and phone = #{phone}


        and status = #{status}

mapper.update:
update orders
cancel_reason=#{cancelReason}, rejection_reason=#{rejectionReason}, cancel_time=#{cancelTime}, pay_status=#{payStatus}, pay_method=#{payMethod}, checkout_time=#{checkoutTime}, status = #{status}, delivery_time = #{deliveryTime}
where id = #{id}
